mapper.select:
select 
from goodsincoming
where incoming_num = #{incomingNum} and goods_num = #{goodsNum}

mapper.update:
update goodsincoming
set incoming_date = #{incomingDate}, incoming_qty = #{incomingQty}, production_date = #{productionDate},
    incoming_price = #{incomingPrice}
where incoming_num = #{incomingNum} and goods_num = #{goodsNum}
